import styled from "styled-components"

const CartIcon = styled.div`
    width: 45px;
    height: 45px;
    position: relative;
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;

    .icon {
        width: 50px;
        height: 50px;
    }
    .item-count {
        position: absolute;
        font-size: 20px;
        font-weight: bold;
        bottom: 2px;
    }
`
export default CartIcon
