import styled from "styled-components"

const CollectionPreview = styled.div`
    display: flex;
    flex-direction: column;
    margin:50px 0 20px 0;
    .title {
        align-self:flex-start;
        font-size: 28px;
        margin-bottom: 25px;
        cursor:pointer;
        &:hover{
            color:grey;
        }
    }
    .preview {
        display: flex;
        justify-content: space-between;
    }
`
export default CollectionPreview
