import { put, all, call, takeLatest } from "redux-saga/effects"
import userActionTypes from "./user.types"
import {
    signInSuccess,
    signInFailure,
    signOutSuccess,
    signOutFailure
} from "./user.actions"

import {
    auth,
    createUserProfileDocument,
    googleProvider
} from "../../firebase/firebase.utils"

function* getSnapShotFromUserAuth(user, additionalData) {
    try {
        const userRef = yield call(createUserProfileDocument, user, additionalData)
        const snapShot = yield userRef.get()
        yield put(signInSuccess({
            id: snapShot.id,
            ...snapShot.data()
        }))
    } catch (error) {
        yield alert(error.message)
        yield put(signInFailure(error))
    }
}
//------------------sign in-------------------
function* signInWithEmail({ payload: { email, password } }) {
    try {
        const { user } = yield auth.signInWithEmailAndPassword(email, password)
        yield getSnapShotFromUserAuth(user)
    } catch (error) {
        yield alert(error.message)
        yield put(signInFailure(error))
    }
}

function* signInWithGoogle() {
    try {
        const { user } = yield auth.signInWithPopup(googleProvider)
        yield getSnapShotFromUserAuth(user)
    } catch (error) {
        yield alert(error.message)
        yield put(signInFailure(error))
    }
}

function* onSignInWithEmailStart() {
    yield takeLatest(
        userActionTypes.SIGN_IN_WITH_EMAIL_START,
        signInWithEmail
    )
}
function* onSignInWithGoogleStart() {
    yield takeLatest(
        userActionTypes.SIGN_IN_WITH_EMAIL_START,
        signInWithGoogle
    )
}
//-----------------sign out--------------------
function* onSignOut() {
    try {
        yield auth.signOut()
        yield put(signOutSuccess())
    } catch (error) {
        yield alert(error.message)
        yield put(signOutFailure(error))
    }
}

function* onSignOutStart() {
    yield takeLatest(
        userActionTypes.SIGN_OUT_START,
        onSignOut
    )
}

export default function* userSagas() {
    yield all([
        call(onSignInWithEmailStart),
        call(onSignInWithGoogleStart),
        call(onSignOutStart)
    ])
}
