import styled from "styled-components"

const CartItem = styled.div`
    width: 100%;
    display: flex;
    height: 150px;
    margin-bottom: 15px;
    text-align:center;

    img {
        width: 25%;
    }
    .detail{
        width: 70%;
        display: flex;
        flex-direction: column;
        align-items: flex-start;
        justify-content: center;
        padding: 10px 20px;
        text-align:left;
    }
    .upper{
        margin-bottom:30px;
        .name{
            font-weight:normal;
            text-transform:uppercase;
            letter-spacing:2px;
        }
    }
    .lower{
        margin-bottom:10px;
    }
`
export default CartItem
