import styled from "styled-components"

const Header = styled.div`
    height:70px;
    font-weight:600;
    color:white;
    z-index:100;
    font-size:1.2em;
    width:100%;
    background-color:#448487;
    position:fixed;
    display:flex;
    align-items:center;
    justify-content:space-between;
    padding:0 10%;
    .right{
        align-items:center;
        justify-content:space-between;
        display:flex;
        width:25%;
        .option{
            margin:0 10px;
        }
    }
    .left{
        display:flex;
        justify-content:space-between;
        width:25%;
        .option{
            margin-right:20px;
        }
    }
    @media(max-width:960px){
        font-size:0.5em;
    }

`
export default Header
