import styled from "styled-components"

const Header = styled.div`
    color:white;
    font-size:1.5em;
    display:flex;
    flex-direction:row;
    justify-content:space-between;
    align-items:center;
    width:100%;
    background-color:#448487;
    padding:10px 0;
    position:fixed;
    .logo{
        width:20px;
    }
    .option{
        margin:0 20px;
    }
`
export default Header
