import React, { useState, useEffect } from "react"
import Header from "./header-style"
import { ReactComponent as Logo } from "../../assets/oasis-logo.svg"

const HeaderComponent = () => {
    const [pos, setPos] = useState("top")
    useEffect(() => {
        document.addEventListener("scroll", e => {
            let scrolled = document.scrollingElement.scrollTop;
            if (scrolled >= 5) {
                setPos("moved")
            } else {
                setPos("top")
            }
        })
    }, [])
    return <Header
        style={{ backgroundColor: pos === "top" ? "rgba(0, 0, 0, 0)" : "rgba(0, 0, 0, .5)" }}
    >
        <div className="left">
            <div className="option">SHOP</div>
        </div>

        <div className="logo-container">
            <Logo className="logo" />
        </div>
        <div className="right">
            <span className="option">LOG IN</span>
            <span className="option">CART</span>
        </div>


    </Header>
}

export default HeaderComponent
