import styled from "styled-components"

const CartItem = styled.div`
    width: 100%;
    display: flex;
    height: 90px;
    margin-bottom: 15px;
    text-align:center;

    img {
        width: 150px;
        height:150px;
    }
    .detail{
        width: 70%;
        display: flex;
        flex-direction: column;
        align-items: flex-start;
        justify-content: center;
        padding: 10px 20px;
    }
`
export default CartItem
