import styled from "styled-components"

const Header = styled.div`
    height:70px;
    font-weight:600;
    color:white;
    z-index:100;
    font-size:1.2em;
    width:100%;
    background-color:#448487;
    position:fixed;
    display:flex;
    align-items:center;
    justify-content:space-between;
    
    .logo-container{
        cursor:pointer;
        padding-right:4%;
        @media(max-width:970px){
            margin-left:10px;
        }
    }
    .option{
        cursor:pointer;
    }
    .left{
        margin-left:200px;
        .option{
            margin-left:50px;
        }
        @media(max-width:970px){
            margin:0;
        }
    }
    .right{
        margin-right:200px;
        display:flex;
        align-items:center;
            .option{
            margin-right:50px;
        }
        @media(max-width:970px){
            margin-right:10px;
        }
    }
    @media(max-width:970px){
        font-size:12px;
    }

`
export default Header
