import styled from "styled-components"

const Header = styled.div`
    color:white;
    font-size:1.2em;
    width:100%;
    background-color:#448487;
    padding:10px 0;
    position:fixed;
    display:flex;
    justify-content:space-between;
    .option{
        margin:0 50px;
    }

`
export default Header
