import styled from "styled-components"

const SignIn = styled.div`
    width:50vw;
    margin:0 auto;
    .buttons{
        display:flex;
        flex-direction:column;
    }
    p,span{
        margin:15px 5px;
    }
    .sign-up{
        color:#b07b58;
        cursor:pointer;
        padding-bottom:5px;
        transition-duration:.4s;
        &:hover{
            border-bottom:2px solid #214142;
        }
    }
`

export default SignIn
