import styled from "styled-components"
const CollectionItem = styled.div`
    display: flex;
    flex-direction: column;
    margin-bottom: 30px;
    .title {
        align-self:flex-start;
        font-size: 28px;
        margin-bottom: 25px;
        cursor:pointer;
        &:hover{
            color:grey;
        }
    }
    .preview {
        display: flex;
        justify-content: space-between;
    }
`
export default CollectionItem
