import styled from "styled-components"

const CustomButton = styled.button`
    margin: 20px auto;
    border: none;
    padding: 10px 44px;
    font-size: 36px;
    color: #111;
    position: relative;

    &::before{
        transition: all 0.85s cubic-bezier(0.68, -0.55, 0.265, 1.55);
        content: '';
        width: 50%;
        height: 100%;
        background: black;
        position: absolute;
        top: 0;
        left: 0;
    }

    & .btn-text{
        color: white;
        mix-blend-mode: difference;
    }
    &:hover{
        &::before{
            background: black;
            width: 100%;
            }
        }
        //rounded btn styles
        &.rounded{
        border-radius:50px;
        & .text-green{
        color:#00F0B5;
        mix-blend-mode: difference;
    }

    &::before{
        border-radius: 50px;
        width: 25%;
        background: #00F0B5;
    }
    &:hover{
        &::before{
            background: #00F0B5;
            width: 100%;
            }
        }	
    }
`
export default CustomButton
